import React from "react";
import "./LoginScreen.css";
import { Link } from "react-router-dom";

const LoginScreen = () => {
  return (
    <div className="login-wrapper">
      {/* Bên trái */}
      <div className="side empty"></div>

      {/* Nội dung chính */}
      <div className="center">
        <div className="login-card">
          <div className="login-header">
            <button className="close-btn">✕</button>
            <h2>Đăng nhập</h2>
          </div>

          <h3>Đăng nhập tài khoản học Catalunya</h3>

          <button className="google-btn">Đăng nhập với G+</button>

          <div className="divider">Hoặc</div>

          <input type="email" placeholder="Nhập email tài khoản học" />
          <input type="password" placeholder="Nhập mật khẩu" />

          <button className="login-btn">Đăng nhập</button>

          {/* Dùng Link thay vì a */}
          <Link to="/forgot-password" className="forgot">
            Quên mật khẩu?
          </Link>

          <p className="signup">
            Chưa có tài khoản ?{" "}
            <Link to="/register">Tạo tài khoản học mới</Link>
          </p>
        </div>
      </div>

      {/* Bên phải */}
      <div className="side empty"></div>
    </div>
  );
};

export default LoginScreen;
