import React from "react";
import "./WelcomeScreen.css"; // CSS riêng cho trang này

function WelcomeScreen() {
  return (
    <div className="welcome-container d-flex flex-column justify-content-center align-items-center text-center">
      {/* Logo + tên */}
      <div className="position-absolute top-0 start-0 p-3 d-flex align-items-center">
        <img src="/images/logo.png" alt="Logo" className="logo" />
        <span className="ms-2 fw-bold text-white fs-5">Catalunya English</span>
      </div>

      {/* Mascot + Text */}
      <div>
        <img src="/images/logo.png" alt="Mascot" className="mascot mb-3" />
        <h2 className="text-dark fw-bold">
          Chào mừng bạn đến với Catalunya English
        </h2>
      </div>
    </div>
  );
}

export default WelcomeScreen;
