import React from "react";
import "./HomeScreen.css";
import { images } from "../assets/images";
import { Link } from "react-router-dom";

const HomeScreen = () => {
  return (
    <div className="home-container">
      {/* Layout chính với 4 phần */}
      <div className="main-layout">
        {/* Phần trái 1/4 - nền xám + logo2 */}
        <div className="left-section">
          <div className="left-logo-section">
            <img src={images.logo2} alt="Logo2" className="logo2" />
            <span className="brand">Catalunya English</span>
          </div>
        </div>
        
        {/* Phần giữa 2/4 - nền trắng */}
        <div className="center-section">
          {/* Menu navigation */}
          <nav className="nav-menu">
            <Link to="/review" className="nav-item">Ôn tập</Link>
            <Link to="/learn" className="nav-item">Học từ mới</Link>
            <Link to="/exercise" className="nav-item">Bài tập</Link>
            <Link to="/offers" className="nav-item">Ưu đãi</Link>
          </nav>

          {/* Main content */}
          <div className="content-box">
            <img src={images.mascot1} alt="Mascot" className="mascot-big" />
            <p className="slogan">Hãy chăm chỉ học từ mới mỗi ngày bạn nhé ❤️</p>
            <button className="btn-learn">Học từ mới</button>
          </div>
        </div>

        {/* Phần phải 1/4 - nền xám + logo1 */}
        <div className="right-section">
          <div className="right-logo-section">
            <img src={images.logo1} alt="Logo1" className="logo1-right" />
          </div>
        </div>
      </div>

      {/* Bảng thông tin */}
      <aside className="sidebar">
        <div className="stat-card yellow">
          <p>Bạn đã học được</p>
          <h3>0 từ</h3>
        </div>

        <div className="stat-card green">
          <p>Bạn đã học liên tục</p>
          <h3>0 ngày</h3>
        </div>
      </aside>
    </div>
  );
};

export default HomeScreen;
