import React, { useState } from "react";
import "./LoginModal.css";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";

const LoginModal = ({ isOpen, onClose, defaultTab = "login" }) => {
  const [activeTab, setActiveTab] = useState(defaultTab);
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [registerData, setRegisterData] = useState({
    name: "",
    email: "",
    password: ""
  });

  const { login, register } = useAuth();
  const navigate = useNavigate();

  const handleLoginChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegisterChange = (e) => {
    setRegisterData({
      ...registerData,
      [e.target.name]: e.target.value
    });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    
    const registeredUser = localStorage.getItem('registeredUser');
    
    if (registeredUser) {
      const user = JSON.parse(registeredUser);
      
      if (loginData.email === user.email && loginData.password === user.password) {
        login(user);
        onClose();
        // Stay on current page, just update auth state
      } else {
        alert("Email hoặc mật khẩu không đúng!");
      }
    } else {
      alert("Tài khoản không tồn tại. Vui lòng đăng ký trước!");
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    
    if (registerData.name && registerData.email && registerData.password) {
      const success = register(registerData);
      if (success) {
        alert("Đăng ký thành công! Hãy đăng nhập.");
        setActiveTab("login");
        setRegisterData({ name: "", email: "", password: "" });
      }
    } else {
      alert("Vui lòng điền đầy đủ thông tin!");
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>
          ×
        </button>
        
        <div className="modal-tabs">
          <button 
            className={`tab-button ${activeTab === "login" ? "active" : ""}`}
            onClick={() => setActiveTab("login")}
          >
            Đăng nhập
          </button>
          <button 
            className={`tab-button ${activeTab === "register" ? "active" : ""}`}
            onClick={() => setActiveTab("register")}
          >
            Đăng ký
          </button>
        </div>

        {activeTab === "login" ? (
          <form onSubmit={handleLogin} className="modal-form">
            <h3>Đăng nhập</h3>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={loginData.email}
              onChange={handleLoginChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Mật khẩu"
              value={loginData.password}
              onChange={handleLoginChange}
              required
            />
            <button type="submit" className="modal-submit-btn">
              Đăng nhập
            </button>
          </form>
        ) : (
          <form onSubmit={handleRegister} className="modal-form">
            <h3>Đăng ký</h3>
            <input
              type="text"
              name="name"
              placeholder="Họ và tên"
              value={registerData.name}
              onChange={handleRegisterChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={registerData.email}
              onChange={handleRegisterChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Mật khẩu"
              value={registerData.password}
              onChange={handleRegisterChange}
              required
            />
            <button type="submit" className="modal-submit-btn">
              Đăng ký
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default LoginModal;
