import React from "react";
import "./LoginScreen.css";
import { Link, useNavigate } from "react-router-dom";

const LoginScreen = () => {
  const navigate = useNavigate();

  const handleClose = () => {
    navigate('/welcome');
  };
  return (
    <div className="login-wrapper">
      {/* 1/4 trái */}
      <div className="side empty"></div>

      {/* 2/4 giữa */}
      <div className="center">
        <div className="login-card">
          {/* Header vàng */}
          <div className="login-header">
            <button className="close-btn">✕</button>
            <h2>Đăng nhập</h2>
          </div>

          {/* Nội dung */}
          <h3 className="title">Đăng nhập tài khoản học Catalunya</h3>

          <button className="google-btn">
            <span className="google-icon">G+</span> Đăng nhập với G+
          </button>

          <div className="divider">Hoặc</div>

          <input type="email" className="email-input" placeholder="Nhập email tài khoản học" />
          <input type="password" className="password-input" placeholder="Nhập mật khẩu" />

          <button className="login-btn" disabled>
            Đăng nhập
          </button>

          <Link to="/forgot-password" className="forgot">
            Quên mật khẩu?
          </Link>

          <p className="signup">
            Chưa có tài khoản ?{" "}
            <Link to="/register">Tạo tài khoản học mới</Link>
          </p>
        </div>
      </div>

      {/* 1/4 phải */}
      <div className="side empty"></div>
    </div>
  );
};

export default LoginScreen;
