import React from "react";
import Header from "../components/common/Header/Header";
import Cloud from "../components/common/Cloud/Cloud";
import mascot from "../assets/images/mascot.png";
import logo from "../assets/images/logo.png";
import cloud1 from "../assets/images/cloud1.png";
import cloud2 from "../assets/images/cloud2.png";
import cloud3 from "../assets/images/cloud3.png";
import "./WelcomeScreen.css";

const WelcomeScreen = () => {
  return (
    <div className="welcome-container">
      {/* Header */}
      <Header logo={logo} title="Catalunya English" />

      {/* Clouds */}
      <Cloud src={cloud1} position="top-left" />
      <Cloud src={cloud2} position="top-right" />
      <Cloud src={cloud3} position="bottom-right" />

      {/* Main layout with left and right sections */}
      <div className="main-layout">
        {/* Left section - Logo và Mascot */}
        <div className="left-section">
          <div className="logo-section">
            <img src={logo} alt="Logo" className="main-logo" />
            <h1 className="brand-title">Catalunya English</h1>
          </div>
          <img src={mascot} alt="Mascot" className="mascot" />
        </div>

        {/* Right section - Text và Buttons */}
        <div className="right-section">
          <h2 className="welcome-text">Chào mừng bạn đến với Catalunya English</h2>
          <p className="slogan">Ghi nhớ 1000 từ vựng trong 1 tháng</p>
          
          <div className="button-group">
            <button className="btn start">Bắt đầu ngay</button>
            <button className="btn login">Mình đã có tài khoản</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeScreen;
