import React from "react";
import "./HomeScreen.css";
import { images } from "../assets/images";
import { Link } from "react-router-dom";

const HomeScreen = () => {
  return (
    <div className="home-container">
      {/* Thanh navbar */}
      <header className="navbar">
        <div className="navbar-left">
          <img src={images.logo1} alt="logo" className="mascot-icon" />
          <span className="brand">Catalunya English</span>
        </div>
        <nav className="navbar-menu">
          <Link to="/review">Ôn tập</Link>
          <Link to="/learn">Học từ mới</Link>
          <Link to="/exercise">Bài tập</Link>
          <Link to="/offers">Ưu đãi</Link>
        </nav>
        <div className="navbar-right">
          <img src={images.logo1} alt="Logo" className="user-icon" />
        </div>
      </header>

      {/* Nội dung chính */}
      <main className="main-content">
        <div className="center-box">
          <img src={images.mascot1} alt="Mascot" className="mascot-big" />
          <p className="slogan">Hãy chăm chỉ học từ mới mỗi ngày bạn nhé &lt;3</p>
          <button className="btn-learn">Học từ mới</button>
        </div>
      </main>

      {/* Bảng thông tin bên phải */}
      <aside className="sidebar">
        <div className="stat-card yellow">
          <p>Bạn đã học được</p>
          <h3>0 từ</h3>
        </div>
        <div className="stat-card green">
          <p>Bạn đã học liên tục</p>
          <h3>0 ngày</h3>
        </div>
      </aside>
    </div>
  );
};

export default HomeScreen;
