import React, { useState } from "react";
import "./LoginScreen.css";
import { Link, useNavigate } from "react-router-dom";

const LoginScreen = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  const handleClose = () => {
    navigate('/');
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const isFormValid = formData.email.trim() !== "" && formData.password.trim() !== "";

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      console.log("Login data:", formData);
      // TODO: Xử lý đăng nhập
    }
  };
  return (
    <div className="login-wrapper">
      {/* 1/4 trái */}
      <div className="side empty"></div>

      {/* 2/4 giữa */}
      <div className="center">
        <div className="login-card">
          {/* Header vàng */}
          <div className="login-header">
            <button className="close-btn" onClick={handleClose}>✕</button>
            <h2>Đăng nhập</h2>
          </div>

          {/* Nội dung */}
          <h3 className="title">Đăng nhập tài khoản học Catalunya</h3>

          <button className="google-btn">
            <span className="google-icon">G+</span> Đăng nhập với G+
          </button>

          <div className="divider">Hoặc</div>

          <form onSubmit={handleSubmit}>
            <input 
              type="email" 
              name="email"
              className="email-input" 
              placeholder="Nhập email tài khoản học"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input 
              type="password" 
              name="password"
              className="password-input" 
              placeholder="Nhập mật khẩu"
              value={formData.password}
              onChange={handleChange}
              required
            />

            <button 
              type="submit"
              className={`login-btn ${isFormValid ? 'active' : ''}`}
              disabled={!isFormValid}
            >
              Đăng nhập
            </button>
          </form>

          <Link to="/forgot-password" className="forgot">
            Quên mật khẩu?
          </Link>

          <p className="signup">
            Chưa có tài khoản ?{" "}
            <Link to="/register">Tạo tài khoản học mới</Link>
          </p>
        </div>
      </div>

      {/* 1/4 phải */}
      <div className="side empty"></div>
    </div>
  );
};

export default LoginScreen;
