import React from "react";
import "./WelcomeScreen.css";
import { images } from "../assets/images";
import { Cloud, Header } from "../components";
import { useNavigate } from "react-router-dom";

const WelcomeScreen = () => {
  const navigate = useNavigate();

  return (
    <div className="welcome-container">
      <Header logo={images.logo} title="Catalunya English" />

      {/* Main content với layout trái-phải */}
      <div className="main-content">
        {/* Mascot bên trái */}
        <div className="left-side">
          <img src={images.mascot} alt="Mascot" className="mascot" />
        </div>

        {/* Text và buttons bên phải */}
        <div className="right-side">
          <p className="slogan">Ghi nhớ 1000 từ vựng trong 1 tháng</p>
          <div className="button-group">
            <button className="custom-btn start" onClick={() => navigate("/home")}>Bắt đầu ngay</button>
            <button className="custom-btn login" onClick={() => navigate("/login")}>Mình đã có tài khoản</button>
          </div>
        </div>
      </div>

      <Cloud src={images.cloud1} position="top-left" />
      <Cloud src={images.cloud2} position="top-right" />
      <Cloud src={images.cloud3} position="bottom-right" />
    </div>
  );
};

export default WelcomeScreen;
